// Your long hex string (without the leading 0x for easier handling)
const hexString = "";

// Function to split the string into chunks of 64 chars (32 bytes)
function splitHexString(str, chunkSize = 64) {
  const chunks = [];
  for (let i = 0; i < str.length; i += chunkSize) {
    chunks.push(str.slice(i, i + chunkSize));
  }
  return chunks;
}

const parts = splitHexString(hexString);

// Assign each chunk to a variable dynamically (optional)
const variables = {};
parts.forEach((chunk, index) => {
  variables[`part_${index + 1}`] = '0x' + chunk;
});

console.log(variables);

// To join them back together:
const joinedHexString = Object.values(variables).map(s => s.slice(2)).join('');
console.log('0x' + joinedHexString);
